package Variable_study;

public class Data_types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declaration
		
   byte number;
   short A;
   int B;
   long C;
   float D;
   double E;
   
   // initilization
   
   number=127;
   A=155;
   B=1000;
   C=100000000;
   D=5000.5342627646456f;
   E=4000.98655734454654;
   
   // usage
   
   System.out.println("Number is "+number);
   System.out.println("Output of A "+A);
   System.out.println("Output of B "+B);
   System.out.println("Output of C "+C);
   System.out.println("Output of D "+D);
   System.out.println("Output of E "+E);
   

	}

}
