package Variable_study;

public class Math_functions {

public static void main(String[] args) {
	
	
		// TODO Auto-generated method stub
		
     //Addition
	
		int a=10;
		int b=58;
		int c=253;
		int sum=(a+b+c);
		System.out.println("Addition of three number is "+sum);
		
		//Percentage
		
		float d=23;
		float e=44;
		float f=82;
		float g=(d+e+f)/300*100;
		System.out.println("Percentage of three number is "+g);


	}
}
